insert into Sach(tenSach,moTa,ngonNgu,hinhAnh,idtacGia,idnhaSanXuat,idLoaiSach,giaBan) values
('Dạy con làm giàu','Một quốc gia có thể tồn tại như thế nào nếu việc dạy trẻ con quản lý tiền bạc vẫn là trách nhiệm của phụ huynh, mà hầu hết họ không có nhiều kiến thức về vấn đề này? Chúng ta phải làm gì để thay đổi số phận tiền bạc lận đận của mình? Nhà giàu đã làm giàu như thế nào từ hai bàn tay trắng?... Cuốn sách Dạy con làm giàu của tác giả Robert T. Kiyosaki sẽ giúp bạn giải đáp được phần nào những thắc mắc trên.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19596.jpg?v=2&w=340&h=497','1','2','kd','59000'),
('Ngôn ngữ bí mật của tiền','Nếu tiền chỉ là các con số, thì chẳng ai trong chúng ta phải mắc nợ hết. Các con số rất đơn giản. Cái phức tạp là chúng ta làm gì với tiền. Chúng ta dùng tiền để vuốt ve cảm xúc, mua sự tôn trọng, thể hiện sự quan tâm ít hay nhiều. Chúng ta không đơn giản là kiếm tiền, chi tiêu và tiết kiệm; chúng ta tán tỉnh, khao khát và khinh bỉ tiền; chúng ta dùng tiền để tự phạt, tự thưởng. Không nhận rõ điều này, chúng ta gán cho tiền ý nghĩa mà nó không có – cái mà cựu chuyên gia tâm lý, hiện đang là giảng viên kinh doanh gọi là “câu chuyện của tiền”. Và trong quá trình chơi câu chuyện về tiền này, chúng ta thường hy sinh đi những thứ quan trọng nhất trong cuộc sống: Sức khỏe, tự do, các mối quan hệ và hạnh phúc. Câu chuyện tiền của bạn là gì? Tại sao bạn cần cuốn sách này? Cuốn sách này sẽ đưa bạn đi một vòng về các giá trị tiền bạc trong từ trong tiềm thức, những xung đột bộ não phải giải quyết với tiền, những nguyên nhân khiến chúng ta có xu hướng lặp đi lặp lại các vấn đề về tiền, và quan trọng nhất, là chúng ta có thể làm gì để thay đổi chúng. Mời các bạn đón đọc Ngôn ngữ bí mật của tiền','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20114.jpg?v=1&w=340&h=497','2','2','kd','40000'),
('Phù thủy sàn chứng khoán (Thế hệ mới)','Cuốn sách gồm một loạt các cuộc phỏng vấn với các nhà giao dịch thành công và nổi tiếng nhất, với nhiều chi tiết nhất về câu chuyện thành công và những bí mật kinh nghiệm chuyên môn của họ. Ngoài ra, cuốn sách không thể thiếu sự thật về sự nghiệp giao dịch của các nhà quản lý quỹ đầu tư vĩ đại nhất, các nhà giao dịch hàng đầu và các nhà đầu tư chứng khoán tư nhân: từ Richard Dennis, huyền thoại của Sàn chứng khoán Chicago kiêm người sáng lập của Turtles; đến nhà vô địch giao dịch Martin “Buzzy” Schwartz; hay William O Neil trứ danh, và cả Paul Tudor Jones, người đứng đầu của quỹ đầu tư với lợi nhuận hằng năm trong 5 năm liên tục là 100%. Qua cuốn sách, bạn đọc sẽ biết: Các phù thủy trên sàn đã “thao túng” thị trường chứng khoán Mỹ bằng cách nào? Mời các bạn đón đọc Phù thủy sàn chứng khoán','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19820.jpg?v=1&w=340&h=497','1','1','kd','62000'),
('Những bài học về đầu tư từ Waren Buffett','Cuốn sách Những bài học đầu tư từ Warren Buffett sẽ là lời giải đáp cho câu hỏi: Điều gì đã làm nên thành công của Warren Buffett, nhà đầu tư trứ danh đến từ Omaha? - một câu hỏi luôn đánh đố nhiều người và là đề tài khai thác của nhiều tác phẩm.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/18586.jpg?v=1&w=340&h=497','2','3','kd','60000'),
('Đánh bại phố Wall','Chìa khóa để đầu tư thành công, theo Lynch, là phải ghi nhớ rằng cổ phiếu không giống như tấm vé số; luôn có một công ty đằng sau mỗi cổ phiếu và một nguyên nhân lý giải cho cách thức vận hành của các công ty - và cổ phiếu của chúng. Lynch chỉ ra làm cách nào chúng ta có thể tìm hiểu tối đa về công ty mục tiêu và xây dựng một danh mục đầu tư sinh lợi dựa trên chính kinh nghiệm, hiểu biết và kết quả nghiên cứu của bản thân. Không có bất kỳ lý do nào cản trở một nhà đầu tư cá nhân tự trở thành chuyên gia, và cuốn sách này sẽ chỉ ra cách thực hiện điều đó.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/15192.jpg?v=3&w=340&h=497','3','2','kd','70000'),
('Sách lược đầu tư của W. Buffett','Warren Buffett được xem là nhà đầu tư thành công nhất thế giới, là cổ đông lớn nhất kiêm giám đốc hãng Berkshire Hathaway, người giàu thứ hai thế giới sau Bill Gates với tài sản hơn 50 tỉ USD. Nổi tiếng do sự kiên định trong triết lý đầu tư, sự nghiệp của đã Warren Buffett để lại muôn vàn bài học kinh doanh quý báu…','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/15288.jpg?v=1&w=340&h=497','4','3','kd','90000'),
('10 điều răn cho doanh nhân','10 điều răn dành cho doanh nhân là cuốn sách về quản trị kinh doanh nhưng chịu ảnh nhiều của đạo Thiên chúa. Cuốn sách giúp độc giả Việt Nam có một cái nhìn mới mẻ về các nguyên tắc quản trị và đạo đức trong kinh doanh để gây dựng và phát triển công ty một cách bền vững.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20264.jpg?v=1&w=340&h=497','1','2','kd','69000'),
('Đầu tư bất động sản','Tác giả cuốn sách – David Lindahl là nhà đầu tư bất động sản tài năng, giám đốc điều hành Tập đoàn Đầu tư Lindahl và Bostonian – công ty đầu tư bất động sản xuyên quốc gia, và cũng là người thành lập công ty RE Mentor – nơi hướng dẫn các nhà đầu tư thu lợi nhuận từ tất cả các hình thức đầu tư bất động sản, đồng thời là diễn giả nổi tiếng tầm cỡ thế giới. Qua cuốn sách, bạn sẽ học được những phương pháp hữu hiệu nhất để đọc và hiểu thị trường đầu tư; thu hút các hợp đồng và nguồn vốn tài trợ; hạn chế rủi ro và thu lợi nhuận lớn... Ngoài ra, bạn sẽ thật sự cảm thấy thu hút bởi những ví dụ từ những trải nghiệm của Donald Trump cùng các lãnh đạo cấp cao của tập đoàn Trump.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/4536.jpg?v=2&w=340&h=497','1','2','kd','30000'),
('Tạo dựng thương hiệu cá nhân','Tác phẩm "Tạo dựng thương hiệu cá nhân" dành cho các nhà quản lý muốn tiến xa hơn, dành cho các bạn trẻ mới ra trường khao khát thể hiện bản thân - và dành cho bất kỳ ai muốn nắm bắt các cơ hội nhờ vào chính năng lực của mình.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20273.jpg?v=1&w=340&h=497','2','2','kd','73000'),
('Bán hàng cho những gã khổng lồ','Ngày nay, việc sắp xếp các cuộc họp bán hàng với những người ra quyết định tại các công ty lớn là vô cùng khó khăn. Nếu thấy điều này là đúng thì bạn không hề đơn độc. Rất nhiều yếu tố thị trường đã cản trở việc tiếp cận với các công ty lớn. Người ta cần những cách tiếp cận mới để đạt được thành công trong thị trường cuồng loạn hiện nay. Bó mình trong những điều đã cũ sẽ chẳng đưa bạn tới đâu – và đặc biệt nếu đích đến là một công ty lớn lại càng xa vời. Các chiến lược bán hàng mới là rất cần thiết để tiếp cận những gà khổng lồ này','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20488.jpg?v=1&w=340&h=497','1','2','kd','30000'),
('Thấu hiểu khách hàng bán hàng hiệu quả','Bán hàng là huyết mạch trong xã hội chúng ta. Dù trực tiếp hay gián tiếp, qui trình bán hàng và mua hàng cũng ảnh hưởng sâu sắc đền hầu hết các khía cạnh của cuộc sống, đặc biệt là vấn đề sự nghiệp, thu nhập và các mối quan hệ. Bởi vậy, việc học hỏi một phương pháp bán hàng khoa học trở thành điều tối cần thiết trong xã hội ngày nay. Nhưng giữa một rừng lý thuyết về bán hàng, điều gì mới thực sự hiệu quả?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20275.jpg?v=1&w=340&h=497','1','3','kd','68000'),
('Ai hiểu khách hàng người đó bán được hàng','Cuốn sách Ai hiểu được khách hàng người đó bán được hàng giới thiệu quá trình nhân viên bán hàng tìm kiếm khách hàng tiềm năng, quá trình chi tiêu của khách hàng, những chi tiết nhỏ mà khách hàng quan tâm, lời nói và hành động, cử chỉ của khách hàng, tâm lí của khách hàng, tín hiệu giao dịch của khách hàng… giúp người bán hàng hiểu được khách hàng, kịp thời đáp ứng nhu cầu của họ, từ đó dễ dàng có được đơn đặt hàng và nâng cao thành tích tiêu thụ sản phẩm.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/10468.jpg?v=2&w=340&h=497','2','3','kd','100000'),
('Bí quyết bán hàng thành công - Sau 5 phút bạn có được đơn đặt hàng','Cuốn sách này với tựa đề “Bí quyết bán hàng thành công – Sau 5 phút bạn có được đơn đặt hàng” đã tổng kết những kiến thức cần thiết, những kỹ năng, kỹ xảo bán hàng vừa đơn giản vừa thực dụng. Điều này giúp người bán hàng nhanh chóng nắm vững, vận dụng thành thạo và sớm hình thành được quan niệm kinh doanh phù hợp với mình, đặt cơ sở vững chắc để trở thành một người bán hàng giỏi.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/505.jpg?v=3&w=340&h=497','1','2','kd','46000'),
('Marketing đột phá','Marketing đột phá là một trong những sách marketing hay, là thành tựu của nhiều tác giả, cuốn sách sẽ giúp bạn khám phá những thay đổi đang diễn ra, qua đó đề xuất những cách mà doanh nghiệp có thể áp dụng để đưa phương pháp mới này vào công tác marketing trong tổ chức mình.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/15290.jpg?v=2&w=340&h=497','2','3','kd','35000'),
('Trở thành thiên tài chơi chứng khoán','Là một cuốn cẩm nang đầu tư, Trở thành thiên tài chơi chứng khoán có tính thực tiễn cao và rất dễ sử dụng. Cuốn sách sẽ hướng dẫn bạn cách khám phá những cơ hội đầu tư mà các chuyên gia thường bỏ qua, cũng như tìm kiếm giá trị ngầm và những khoản lợi nhuận cực kỳ lớn nằm ở những nơi ít nhà đầu tư để ý.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/4359.jpg?v=3&w=340&h=497','2','1','kd','100000'),
('Viết lại luật chơi','Đây là cẩm nang cho những ai không bằng lòng với việc theo sau, cho những ai tin rằng, cách tốt nhất để chiến thắng là viết lại luật chơi, cho những ai không sợ hãi khi thách thức tính chính thống, cho những ai có thiên hướng xây dựng hơn là cắt bỏ, cho những ai quan tâm đến việc tạo ra khác biệt hơn là tạo dựng sự nghiệp và cho những ai cam kết dốc sức hoàn toàn cho việc đánh dấu lãnh thổ tương lai trước nhất','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20215.jpg?v=1&w=340&h=497','3','3','kd','120000'),
('Nghĩ giàu làm giàu & trải nghiệm ở Việt Nam','Hãy đọc Nghĩ Giàu Làm Giàu & Trải Nghiệm Ở Việt Nam và thay đổi cuộc đời bạn như những gì Napoleon Hill đã làm! Nghĩ Giàu Làm Giàu đã làm thay đổi rất nhiều cuộc đời của những người đã đọc nó trở thành kỳ diệu. Với giáo sư Charles H. Cranford lại càng đặc biệt bởi ông đã có thời gian làm việc cùng Napoleon Hill, cho nên ông viết Nghĩ Giàu Làm Giàu - những trải nghiệm ở Việt Nam với vai trò một cộng sự của Napoleon Hill, cũng là người từng vấp ngã nhiều lần trên phương diện tài chính như ông ấy và nhờ vậy mà khi nhìn lại, ông nhận ra rằng những sai lầm do cố tìm cách để thành công giờ đây lại mách cho mình biết rằng mình phải làm gì và làm như thế nào để thành công.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/12043.jpg?v=2&w=340&h=497','4','5','kd','98000'),
('Chuyển giao quyền lực','Chuyển giao quyền lực là cuốn sách ba trong một hoành tráng nói về những thay đổi của thời đại chúng ta, sử dụng một cách thuyết phục rất nhiều ví dụ về cách mà thông tin và công nghệ đang thay đổi việc kinh doanh, chính trị và xã hội nói chung. Mặc dù một vài quan sát và sự thấu hiểu bản chất của Toffler rất dễ thấy và quen thuộc, chúng vẫn cung cấp một bức tranh hấp dẫn và hữu ích cho từng ngành nghề và sở thích của mọi người.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20193.jpg?v=1&w=340&h=497','6','4','kd','88000'),
('Bơi cùng cá mập mà không bị nuốt chửng','Nếu bạn vừa giở cuốn sách này và không chút hy vọng vào giá trị của nó, thì bạn đã nhầm. Bơi cùng cá mập mà không bị nuốt chửng không phải là một trong những bộ sưu tập vô ích những tư tưởng thông thái trong kinh doanh, mà là một kho báu vô giá những bài học có thể áp dụng ngay vào công việc kinh doanh, vào cuộc sống, các mối quan hệ và các mục tiêu của bạn. Tôi có cảm giác phân vân khi vừa muốn chia sẻ tài năng, sự thấu hiểu và tính sáng tạo học được từ Harvey Mackay, lại vừa muốn giữ nó cho riêng mình. Trong khi tổng kết ra những bài học cho cá nhân mình, tôi nhận ra rằng, nếu giữ cho riêng mình cuốn sách này, thì tôi sẽ độc chiếm toàn bộ sân. Và tôi biết, các bạn cũng sẽ không bao giờ muốn đối thủ cạnh tranh biết đến cuốn sách tuyệt vời này.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/2118.jpg?v=1&w=340&h=497','7','4','kd','43000'),
('Tôi đã kiếm 2 triệu đô từ thị trường chứng khoán như thế nào?',' Nocolas Darvas là một tỷ phú kỳ tài bậc nhất thế giới và được tôn vinh là chiến lựơc gia trên thị trường chứng khoán. Các bài học kinh nghiệm về đầu tư chứng khoán của ông được gọi là Nguyên lý Darvas, Phương pháp đầu tư Darvas, Nguyên tắc kinh doanh chứng khoán Darvas đã trở thành kinh điển và là một trong những nội dung đào tạo, tham khảo không thể thiếu được của những người muốn đầu tư chứng khoán. Ông đồng thời là tác giả cuốn sách Phố Wall - một Las Vegas khác.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/4278.jpg?v=3&w=340&h=497','4','4','kd','30000');
insert into Sach(tenSach,moTa,ngonNgu,hinhAnh,idtacGia,idnhaSanXuat,idLoaiSach,giaBan) values
('Sức mạnh của thói quen','Sức mạnh của thói quen sẽ làm bạn say mê bởi những ý tưởng thú vị, những nghiên cứu ấn tượng, những phân tích thông minh và những lời khuyên thiết thực. Những độc giả đưa cuốn sách này vào danh sách bestseller của Thời báo New York suốt 40 tuần đã kiểm chứng điều đó. Và dưới đây, Alpha Books cũng xin chích đăng một vài bình luận về cuốn sách: “Một thăm dò thú vụ về những thói quen bệnh hoạn của chúng ta – chúng ta hút thuốc, chúng ta không ngừng kiểm tra điện thoại, liên tục chọn lựa người tình tồi, chúng ta thường xuyên (hoặc không bao giờ) thu dọn phòng ốc. Duhigg đào sâu vào tìm ra nguyên do tại sao chúng ta làm như vậy, và chúng ta có thể thay đổi chúng ra sao, với cả cá nhân và tổ chức” – The Dail','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/2392.jpg?v=2&w=340&h=497','6','4','kn','38000'),
('Nhật ký 300 ngày ở Harvard - Học để thay đổi thế giới','Quyển sách không đưa ra câu trả lời cụ thể mà gợi mở những ý tưởng đang thịnh hành ở Mỹ để người đọc tò mò và tìm hiểu thêm bằng cách Google nhiều nguồn khác nhau. Tựa đề quyển sách là khẩu hiệu của trường Giáo Dục Harvard, với ý nghĩa đơn giản là không có điểm dừng cho quá trình cải thiện thế giới mình đang sống và việc tự học hỏi chính là chìa khóa mở ra cánh cửa đổi mới: “HỌC ĐỂ THAY ĐỔI THẾ GIỚI”.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/15078.jpg?v=1&w=340&h=497','5','4','kn','200000'),
('Cuốn sách số 1 về tìm việc','Cuốn Sách Số 1 Về Tìm Việc của tác giả Lynn Williams được viết dành cho tất cả những ai đang trải qua quá trình tìm việc, hoặc đang có ý định thay đổi công việc. Nếu bạn đang tìm kiếm một cơ hội tốt hơn, nếu bạn vừa tốt nghiệp và lần đầu tìm việc, hoặc nếu bạn bị buộc phải tìm việc do cắt giảm nhân sự ở công ty cũ, đây sẽ là cuốn sách hữu ích hướng dẫn bạn trả lời những câu hỏi căn bản nhất để tìm được một công việc tuyệt vời.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/9846.jpg?v=2&w=340&h=497','3','3','kn','22000'),
('Thức dậy và mơ đi','Thức dậy và mơ đi được viết lên lên để khơi gợi và động viên sự thay đổi diễn ra bên trong bạn. Chính bạn sẽ là người quyết định điều gì tốt đẹp nhất cho mình. Xuyên suốt cuốn sách là những công cụ, phương pháp giúp bạn tạo nên những đột phá trong cuộc sống và tự tin bước trên con đường mình đã chọn.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/12224.jpg?v=1&w=340&h=497','2','2','kn','68000'),
('Con đường thành đạt của phụ nữ không phải ở nơi bạn bắt đầu mà là nơi bạn kết thúc','Nếu bạn đang gặp khó khăn, thử thách hay bạn đang mất phương hướng, hãy đọc câu chuyện và những lời khuyên của người phụ nữ thành đạt này. Chắc chắn quyển sách sẽ cho bạn nhiều ý tưởng thú vị để có thể thay đổi tương lai và mang đến cho bạn một cuộc sống tốt đẹp hơn.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/14363.jpg?v=3&w=340&h=497','2','1','kn','42000'),
('Du học Nhật Bản','Thời toàn cầu hoá, những cuốn sách kể về những trải nghiệm chân thực và chi tiết như thế này rất cần thiết. Nếu bạn là người trẻ đang tìm kiếm cơ hội du học nhưng gia đình không có điều kiện; nếu bạn đang muốn học hỏi kinh nghiệm làm hồ sơ hay phỏng vấn xin học bổng; nếu bạn muốn tìm hiểu về kỹ năng sống, học tập, làm việc của sinh viên ở nước ngoài và đặc biệt nếu bạn muốn tìm hiểu về ngôn ngữ, đất nước, con người Nhật Bản tươi đẹp, hấp dẫn nhưng cũng vô cùng xa vời thì Du Học Nhật Bản của Phi Hoa chính là cuốn sách bạn cần đọc.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/14352.jpg?v=2&w=340&h=497','6','5','kn','66000'),
('Tôi muốn cuộc đời như tôi muốn','Ờ trước khi bắt đầu, tôi nói cho các cậu hay, đây là cuốn sách kể lại hành trình trở thành doanh nhân của tôi. Được rồi, được rồi, có thể thi thoảng tôi hơi chém một tí, nhưng dù gì thì nó cũng là câu chuyện của tôi. Vì thế, các cậu nghe thì nghe, mà không nghe thì kệ, tôi cũng đếch quan tâm đâu.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/13728.jpg?v=2&w=340&h=497','4','4','kn','64000'),
('Đứng dậy mạnh mẽ','Cuốn sách này không chỉ là một cẩm nang hữu ích cho các bạn tuổi mới lớn, cho các học sinh, sinh viên, những đối tượng dễ bị ảnh hưởng bởi sự bắt nạt mà còn là sự tham khảo cần thiết đối với các thầy cô giáo, các bậc cha mẹ và những người làm công tác xã hội. Những câu chuyện cũng như các kỹ năng được chia sẻ trong cuốn sách này có thể góp phần thúc đẩy sự phát triển tinh thần của mỗi bạn đọc. Hy vọng rằng khi gấp cuốn sách lại bạn đọc có thể cảm thấy tự tin hơn, mạnh mẽ hơn, được chuẩn bị tốt hơn để đương đầu không chỉ với sự bắt nạt mà những thách thức khác trong cuộc sống.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/14366.jpg?v=2&w=340&h=497','6','2','kn','98000'),
('Chiến thắng con quỷ trong bạn','Cuốn sách Chiến thắng con quỷ trong bạn là cuộc trò chuyện của Napoleon Hill và Con Quỷ. Sau bao nhiêu năm miệt mài nghiên cứu cuối cùng ông cũng phát hiện ra Con Quỷ, bắt nó phải thú tội và tiết lộ những sự thật kinh hoàng về nơi nó sống, cách nó kiểm soát tâm trí con người và cách để con người chiến thắng được nó','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/9672.jpg?v=2&w=340&h=497','3','1','kn','100000'),
('Tuần làm việc 4 giờ','Cuốn sách THE 4-HOUR WORK WEEK đã làm dấy lên một làn sóng tư tưởng mới mẻ tại thung lũng Silicon. Marc Andreesen - chuyên gia về tối ưu hoá năng suất làm việc cá nhân đồng thời là một trong những doanh nhân nổi tiếng nhất ở trung tâm công nghệ cao của thế giới trong một bài phỏng vấn cho biết: Về cơ bản, việc Tim đã làm là tập hợp và chắt lọc những học thuyết về quản lý thời gian cùng năng suất lao động trong vòng 2, 3 thập kỷ vừa qua, sau đó tối ưu hoá nó và đưa ra những khái niệm mới, rất hữu dụng','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/10006.jpg?v=2&w=340&h=497','7','5','kn','120000'),
('Thay đổi hay là chết','“Nếu muốn có cuộc sống tuyệt vời, bạn phải sẵn sàng đương đầu với khó khăn” - nguyên tắc tưởng rằng quen thuộc này hóa ra lại bị chối bỏ bởi rất nhiều người. Phần lớn chúng ta muốn đạt được chiến thắng to lớn, vĩ đại, nhưng lại không thể chịu nổi một thách thức nhỏ. Chúng ta ích kỷ, hèn nhát, đa phần không dám thay đổi và thoát khỏi vùng an toàn. Nếu bạn đang nhận ra mình trong những miêu tả vừa qua, thì đã đến lúc bạn cầm lên cuốn sách của Alexi Panos và Preston Smiles. Bộ đôi tác giả trẻ tuổi, tràn ngập nhiệt huyết với cuộc sống này sẽ cùng bạn “lột xác” trong tác phẩm “Thay đổi hay là chết”.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19709.jpg?v=1&w=340&h=497','2','1','kn','200000'),
('Mặc kệ nó, làm tới đi!','Bạn sẽ làm gì khi đứng trước những trở ngại và thách thức trong cuộc sống? Bạn sẽ làm gì khi đưa ra những quyết định khó khăn? Khi gặp nguy hiểm, bạn sẽ lùi bước hay đương đầu với nó? Bạn giải quyết vấn đề tiền bạc ra sao? Hay nhận thức các vấn đề toàn cầu như thế nào?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/2397.jpg?v=4&w=340&h=497','5','3','kn','80000'),
('NLP căn bản',' Đã bao giờ bạn tự hỏi tại sao cùng đối mặt với một vấn đề mà kết quả mọi người thu được lại rất khác nhau? Tại sao một số người lại thành công ở mọi mặt, tràn đầy nhiệt huyết và luôn tìm thấy những niềm vui bất tận, còn những người khác thì không?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/1855.jpg?v=2&w=340&h=497','7','4','kn','40000'),
('Cuộc sống không giới hạn','Quyển sách không chỉ đơn giản kể lại câu chuyện của cậu bé Nick không tay, không chân vượt qung khó khăn trong cuộc sống như thế nào, để có được cuộc sống tràn ngập tiếng cười ngày hôm nay, Nick đã có những ngày tháng đầy nước mắt như thế nào. Vượt lên trên hết là nghị lực phi thường, sự mạnh mẽ không biên giới, ý chí vượt thoát khỏi hoàn cảnh nghiệt ngã của số phận.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/11293.jpg?v=1&w=340&h=497','3','1','kn','140000'),
('Phương pháp học tập hiệu quả','Khi bắt đầu, là một học sinh đang được đào tạo để trở nên thông minh, bạn có thể cảm thấy rằng mình dành nhiều thời gian và công sức hơn. Bạn sẽ như vậy. Bạn vẫn đang học những điểm tốt của một phương pháp tiếp cận hoàn toàn mới với trường học và đưa vào thực hành. Bạn cũng trải nghiệm sự thay đổi căn bản trong quan điểm về giáo viên và chính mình. Thay đổi là điều khó khăn và chúng ta phải kiên định với nó. Nhưng nếu thử áp dụng theo cuốn sách này, trong thời gian ngắn bạn sẽ thấy sự tiến bộ to lớn của mình về những điều bạn học được và thứ hạng bạn nhận được. Khi việc học trở nên dễ dàng hơn và thứ hạng của bạn được cải thiện, bạn sẽ đi từ thành công này tới thành công khác.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/16484.jpg?v=1&w=340&h=497','5','3','kn','23000'),
('Tư duy logic','Kanbe – nhân vật chính trong cuốn sách, vào những năm cuối tuổi 20 của cuộc đời, một ngày cô chợt nhận ra, trong khi các bạn cùng trang lứa với cô đã và đang gặt hái nhiều thành thì bản thân cô đang dần chững lại trong sự nghiệp. Sau một thời gian suy nghĩ, cô quyết định từ bỏ công việc hiện tại, đi học thêm bằng MBA và đầu quân cho một công ty. Một chương mới tươi sáng hơn được mở ra, và tất cả bắt nguồn từ việc thay đổi nhận thức và tư duy của cô gái trẻ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/16467.jpg?v=2&w=340&h=497','7','3','kn','90000'),
('Học như một thiên tài','Học hỏi là bản năng của loài người, nhưng lại rất ít người thực sự biết phối hợp bộ não, trái tim và cơ thể để tạo nên tri thức mới. Đa phần chúng ta luôn nghĩ học là một khái niệm mơ hồ. Học là đọc lại một quyển sách giáo khoa? Giải những bài mẫu? Học thuộc lòng? Nếu không thể thực hành kiến thức vào đời thực thì đó có phải là học không?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/17477.jpg?v=1&w=340&h=497','7','4','kn','38000'),
('Kỹ năng tư duy logic','Cuốn sách "Kỹ năng tư duy logic" được viết bởi Shibamoto Hidenori để làm sáng tỏ những thắc mắc “Làm thế nào để tư duy tốt?” hay “Phải luyện tập tư duy như thế nào?” của bạn. Cuốn sách được trình bày súc tích, đi từ tổng quan đến chi tiết, giúp bạn nắm bắt được nội dung cốt lõi của 5 kỹ năng tư duy dành cho doanh nhân một cách dễ dàng. Bên cạnh đó, tác phẩm còn bật mí những cách áp dụng kỹ năng tư duy vào công việc để hiệu quả lao động của bạn được nâng lên mức cao nhất.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/18770.jpg?v=1&w=340&h=497','6','4','kn','22000'),
('Phương pháp tối ưu hóa trí nhớ','Tất cả chúng ta đều mong muốn sở hữu kỹ năng ghi nhớ thật tốt. Nhưng lại không biết cách làm thế nào để cải thiện trí nhớ của mình. Trong thời buổi mà con người ngày thêm phụ thuộc vào các công cụ tìm kiếm trên Internet và các “bộ nhớ ngoài” (máy tính, điện thoại,USB…) – khả năng ghi nhớ của chúng ta đang liên tục sụt giảm. Nhưng tại sao lại có những người chỉ cần học 5 ngày là hiểu cả quyển sách 500 trang trong khi có nhữngngười học cả tháng vẫn không nắm được vài trang bài học? Bạn có thể nghĩ tiêu cực: “Mình vốn sinh ra đã có trí nhớ kém rồi, không thể thay đổi được”, hay “Những người nhớ tốt là vì bẩm sinh họ đã vậy rồi!”. Hãy nhanh chóng gạt bỏ kiểu tư duy chấp nhận số phận đó ngay bây giờ, bởi cuốn sách của Zino Kabasawa sẽ chứng minh cho bạn thấy:“Năng lực trí nhớ không phải bẩm sinh mà hoàn toàn có thể rèn luyện được”.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/17210.jpg?v=1&w=340&h=497','5','4','kn','68000'),
('Thuật hùng biện','Khả năng diễn đạt và thuyết phục càng tốt thì bạn càng hài lòng về bản thân. Điều này tỷ lệ thuận với sự lạc quan cũng như tự tin của bạn hay thái độ tích cực, dễ chịu với mọi người xung quanh. Bạn cũng sẽ vui vẻ, thoải mái hơn khi làm bất cứ việc gì.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/15201.jpg?v=1&w=340&h=497','6','3','kn','98000');
insert into Sach(tenSach,moTa,ngonNgu,hinhAnh,idtacGia,idnhaSanXuat,idLoaiSach,giaBan) values
('Đừng cố làm những bà mẹ hoàn hảo','Phụ nữ vẫn tưởng rằng, hình mẫu người vợ, người mẹ hoàn hảo sẽ giúp gia đình thêm hạnh phúc và trọn vẹn. Nhưng thực tế thì hoàn toàn ngược lại. Khi theo đuổi sự hoàn hảo, các chị em sẽ đưa ra những nguyên tắc cứng nhắc đối với chồng và những đứa con của mình. Điều này khiến không khí gia đình trở nên nặng nề, các thành viên bất đồng quan điểm sống và trở nên xa cách.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/16946.jpg?v=1&w=340&h=497','3','2','gd','40000'),
('Đàn ông +','Đàn ông giống như cây bút chì. Thứ tạo ra giá trị không phải là nước sơn bóng bẩy và đẹp đẽ bên ngoài mà là phần lõi xám xịt bên trong. Hoặc sẽ bị chà xát rồi mòn dần theo năm tháng. Hoặc không, đẹp đẽ, nguyên vẹn, mà chẳng được cái tích sự gì.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20428.jpg?v=1&w=340&h=497','4','2','gd','40000'),
('Đứa con muộn','Với danh hiệu là “nhà văn tuổi trẻ”, Alexin đưa đến cho người đọc những tác phẩm truyện vừa với giọng văn hồn nhiên, hài hước, yêu đời hệt như lứa tuổi thanh thiếu niên nhưng lại không thiếu các nút thắt đầy thú vị, kịch tính đủ sức hấp dẫn cả người lớn. Và đằng sau những câu chuyện đó, những câu chuyện xoay quanh thế hệ trẻ nói riêng và con người, xã hội Xô Viết nửa sau thế kỷ XX nói chung; là những bài học triết lý sâu sắc, giàu ý nghĩa. Những đạo lý sâu sắc đó không chỉ dành cho trẻ em hay những cô cậu tuổi teen mà cả những người lớn cũng cần biết, biết để sống tốt hơn và quan trọng hơn là để yêu thương đúng cách, để thấu hiểu con, đặc biệt là những đứa trẻ đang ở độ tuổi vị thành niên vốn cần rất nhiều sự quan tâm, thấu hiểu và cần có cách đối xử tôn trọng, tinh tế.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19348.jpg?v=1&w=340&h=497','4','4','gd','55000'),
('Bản đồ tư duy cho trẻ thông minh - Tăng cường trí nhớ và khả năng tập trung','Các em hãy yên tâm là những gì các em đã đưa vào đầu thì sẽ luôn còn mãi. Vấn đề các em cần làm là giúp bộ não sắp xếp lại thông tin cho gọn gàng để dễ dàng truy cập khi cần sử dụng đến. Tăng cường trí nhớ và khả năng tập trung sẽ giúp các em trở thành chủ nhân thực sự đối với chính trí nhớ của mình và có thể nhớ mọi điều mà mình muốn trong bất cứ tình huống nào.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/13736.jpg?v=2&w=340&h=497','4','5','gd','43000'),
('Chăm sóc thị lực cho trẻ','Cuốn sách này bổ sung những bài tập thị lực, hướng dẫn các trò chơi cải thiện thị lực, tư vấn thay đổi điều kiện môi trường… nhằm hoàn thiện phương án chăm sóc và bảo vệ mắt cho trẻ. Hãy để trẻ tận hưởng trải nghiệm chăm sóc mắt lý thú qua: Liệu pháp massage, trò chơi cho não, trò chơi vận động nhãn cầu, trò chơi cảm xúc, trò chơi tưởng tượng, trò chơi ánh sáng, trò chơi màu sắc... từ cuốn sách này.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/14781.jpg?v=1&w=340&h=497','6','4','gd','42000'),
('Cha nên dạy con như thế nào?','Cuốn sách vận dụng những quan điểm mới, ví dụ sinh động thực tế, kết hợp với phương pháp giáo dục mới, đưa ra cách giải quyết vấn đề hiệu quả, ngôn ngữ đơn giản, tinh tế, dễ hiểu, cung cấp các phương pháp dạy con bổ ích và phong phú. Hi vọng các ông bố trở thành người cha thành công và con cái trở thành những đứa trẻ ưu tú, xuất sắc.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/14777.jpg?v=1&w=340&h=497','6','1','gd','43000'),
('Cha mẹ là người thầy đầu tiên tốt nhất','Mỗi bậc cha mẹ đều nên hiểu rằng, trẻ nhỏ đang ở trong giai đoạn hình thành của phẩm chất đạo đức và những thói quen hành vi, chúng rất giỏi bắt chước theo hành động và lời nói của người lớn, đồng thời lấy lời nói và hành động của cha mẹ làm tấm gương để mình học tập. Cho nên, cha mẹ hãy là một tấm gương tốt cho trẻ. Trên thực tế, cha mẹ chính là tấm gương của trẻ nhỏ, những truyền ngôn từ cha mẹ là cách giáo dục hiệu quả và sinh động nhất đối với trẻ nhỏ. Khi đối mặt với muôn vàn những vấn đề của trẻ (nói dối, cẩu thả, ăn cắp, đố kỵ, nhút nhát....), cha mẹ cần có tinh thần lạc quan, tích cực và những đối sách kịp thời, khoa học.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/10470.jpg?v=2&w=340&h=497','7','3','gd','53000'),
('Đọc vị mọi vấn đề của trẻ','Đọc vị mọi vấn đề của trẻ giúp hàng ngàn cha mẹ giải quyết vấn đề. Với cuốn sách này, bạn cũng có thể tận dụng sự hiểu biết, lời khuyên và cả những thủ thuật làm cha mẹ từ bậc thầy vô cùng yêu trẻ Tracy Hogg. Bà đã nhắc cho chúng ta nhớ rằng: Vấn đề thực ra chỉ là tình huống đòi hỏi một giải pháp sáng tạo. Hãy đặt ra những câu hỏi đúng và bạn sẽ có được câu trả lời đúng.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/12555.jpg?v=2&w=340&h=497','5','2','gd','49000'),
('Mẹ ơi, con sợ đến trường','Mẹ ơi, con sợ đến trường” giới thiệu 87 tình huống, 87 vấn đề rắc rối thường gặp trong cuộc sống và học tập của học sinh Tiểu học giúp các bậc cha mẹ tìm hiểu và giải quyết những vấn đề khó trong cuộc sống và học tập của trẻ trong giai đoạn Tiểu học.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/13976.jpg?v=2&w=340&h=497','5','2','gd','51000'),
('Ma lực hấp dẫn hai giới','Cuốn sách Ma lực hấp dẫn hai giới của tác giả Nguyễn Thị Hồng Khanh dựa trên góc độ phân tích những kiểu đàn ông và phụ nữ khác nhau nhằm đưa đến những kiến giải làm thế nào để vợ chồng hiểu nhau hơn, làm thế nào để điều hoà mâu thuẫn vợ chồng, cùng chia sẻ những gánh nặng của cuộc sống để có một mái ấm hạnh phúc dài lâu.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/452.jpg?v=4&w=340&h=497','6','2','gd','53000'),
('Nụ hôn đầu - Phần 4','Có khi... Run run then thùng nhận nụ hôn đầu, rồi chợt thấy thất vọng vì nó chẳng lãng mạn như thơ ca vẫn nói. Thấy hụt hẵng chỉ vì đứa bạn thân bỗng dưng chơi thân với một đứa khác hơn minh. Đau khổ vì tình yêu đầu đời tan vỡ... Và... Vui sướng có được người bạn tốt ở một nơi xa lạ đầy bỡ ngỡ Hạnh phúc khi nhận ra ta đang có một gia đình can đảm chọn làm điều đúng dù có khi chỉ đơn độc một mình. ... Tất cả những điều đó bắt đầu từ những câu chuyện rất thật, đầy xúc động trong nụ hôn đầu - một món quà dành tặng cho những người ta yêu. Mời các bạn đón đọc sách Nụ hôn đầu - Phần 4.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/7272.jpg?v=3&w=340&h=497','6','3','gd','42000'),
('Bạn đã bao giờ hôn bố mẹ chưa','Cuốn sách là một món quà gợi nhắc lại những kỷ niệm về cha mẹ thời ấu thơ, những hương vị đã theo cùng năm tháng mà qua thời gian, những người trưởng thành dễ bị lãng quên. Đó là những gì thân thương, bình dị nhất như mùi vị món ăn mẹ đã nấu, mùi mái tóc mùi bồ kết của mẹ, hay cú ngã đầu tiên khi lần đầu con đạp xe về phía bố,... chất liệu được lấy từ cuốc sống nên ai cũng có thể thấy một phần hình ảnh của mình trong đó. Những điều tưởng chừng rất nhỏ nhưng được tác giả khéo léo gợi nhắc bằng những câu chữ nhẹ nhàng, bình dị... Giọng kể chuyện tự nhiên, nhẹ nhàng, khoan thai, giàu xúc cảm dễ chiếm được cảm tình của độc giả.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19200.jpg?v=1&w=340&h=497','6','5','gd','40000'),
('Ung thư - Sự thật, hư cấu, gian lận và những phương pháp chữa lành không độc hại','Những gì bạn biết về ung thư đã được tạo dựng cẩn thận và chế tác dưới tay các nghệ sĩ tuyên truyền trong ngành công nghiệp dược phẩm, tất cả nhằm mục đích không cho bạn biết về tính ưu việt to lớn của Mẹ Tự nhiên trong điều trị ung thư. Tin tốt lành là cuốn sách này, “Ung thư – Sự thật, hư cấu, gian lận và những phương pháp chữa lành không độc hại”, sẽ đem lại sự thật. Nó sẽ cho bạn biết về các liệu pháp điều trị ung thư thay thế thực sự hiệu quả.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19527.jpg?v=1&w=340&h=497','6','1','gd','92000'),
('Dinh dưỡng - Chìa khóa vàng cho sức khỏe','Dinh Dưỡng - Chìa Khóa Vàng Cho Sức Khỏe là bộ sách cung cấp cho bạn đọc những kiến thức cơ bản nhất, đầy tính khoa học về các dưỡng chất trong thực phẩm. Chúng ta sẽ cùng tìm hiểu chi tiết các chất dinh dưỡng – chất đạm (protein), chất béo (lipid), chất đường (carbohydrate), chất xơ, và vi chất dinh dưỡng (vitamin và khoáng chất), dưỡng chất đa lượng và vi lượng… ; cách cơ thể sử dụng các chất dinh dưỡng ấy, cách tính toán nhu cầu năng lượng và nguồn tiếp nạp phù hợp.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/11704.jpg?v=2&w=340&h=497','3','4','gd','32000'),
('Tương lai con trong tay bạn','Khác với các cuốn sách về nuôi dạy con khuyên nhủ bạn phải “giữ kiên nhẫn”, “giữ bình tĩnh”…; “Tương lai con trong tay bạn” của tác giả Carolyn Crowder & Audrey Ricker sẽ có một cách tiếp cận hoàn toàn khác và thật sự thực tế, để bạn uốn nắn con phát triển theo hướng tích cực và lành mạnh nhất. Đọc xong tác phẩm này, bạn sẽ rút ra được cách phản ứng phù hợp nhất mỗi khi con tỏ ra ngỗ ngược, và dần dần, tính ngang bướng của con trẻ sẽ không còn là nỗi trăn trở của bạn nữa.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/17213.jpg?v=1&w=340&h=497','5','2','gd','40000'),
('Nuôi con không phải là cuộc chiến (Phần 2)','Cuốn sách không là cẩm nang để bé ăn nhiều tăng cân nhanh hay dạy bé nghe lời răm rắp, mà giúp bạn hiểu con mình hơn. Giúp bạn hiểu chu kỳ sinh học của con và cách phối kết hợp cuộc sống gia đình với chu kỳ sinh học đó của bé. Hơn thế, cuốn sách còn hướng dẫn bạn cách cho ăn khi con đói, các thông tin kinh nghiệm và các trường hợp thực tế áp dụng thành công của các “bà mẹ tuyệt vọng” khác giúp bạn có thông tin cũng như nghị lực thay đổi cách áp dụng nuôi và dạy con ở gia đình. Suy cho cùng, nuôi không hẳn đã khó, đến đoạn dạy con còn khó hơn nhiều.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/9916.jpg?v=2&w=340&h=497','7','3','gd','64000'),
('Thai nghén, sinh đẻ và chăm sóc em bé','Cuốn sách “Mang thai, sinh đẻ và chăm sóc em bé”sẽ hỗ trợ bạn trong việc xử lý các tình huống có thể xảy ra trong quá trình mang thai, sinh và chăm sóc con, giúp bạn có được khoảng thời gian hạnh phúc và thư thái cho chính bản thân, đồng thời cũng giúp bé của bạn có được sự chăm sóc tốt nhất từ cha mẹ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/328.jpg?v=2&w=340&h=497','6','4','gd','74000'),
('1001 bí quyết giữ gìn tình yêu','Cuốn sách “1001 bí quyết giữ gìn tình yêu” của tác giả Đại Minh đưa ra một số tham khảo hữu ích giúp các bạn trẻ tránh được những sai lầm đáng tiếc, biết cách giữ lửa cho tình yêu của mình để cuộc sống lứa đôi luôn êm ấm, hạnh phúc. Có thể xem cuốn sách như một cẩm nang cho tình yêu thời hiện đại, giúp bạn nhìn ra những điều đặc biệt quan trọng trong tình yêu và cùng bạn gìn giữ tình yêu đẹp cho cuộc đời mình.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/450.jpg?v=3&w=340&h=497','6','2','gd','62000'),
('Sổ tay sức khỏe người cao tuổi - Gắn bó tuổi già','Chăm sóc sức khoẻ người cao tuổi ngày càng được xã hội quan tâm và chú trọng, nhất là trong các gia đình sống nhiều thế hệ như xã hội Việt Nam. Ông bà có khoẻ mạnh thì con cháu mới hạnh phúc. Con cháu phải có kiến thức để chăm sóc ông bà, cha mẹ được tốt nhất','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/18620.jpg?v=1&w=340&h=497','7','3','gd','60000'),
('Bé Gấu 5 tuổi đã dạy tôi những gì','Chúng ta thường nghĩ rằng, nuôi dạy con là quá trình ta giáo dục con cái, nhưng thực ra phụ huynh giáo dục chính bản thân mình còn nhiều hơn. Sống cùng bé Gấu, tôi thấy được hình ảnh phản chiếu tính cách của chính mình, đồng thời cũng từ đó mà sửa đổi tâm tính. Vì con, những người làm cha làm mẹ như chúng ta hãy cố gắng để bản thân tốt hơn lên. Đó sẽ là điều đẹp nhất trong hành trình của cha mẹ và con cái.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/18165.jpg?v=1&w=340&h=497','5','4','gd','34000');
insert into Sach(tenSach,moTa,ngonNgu,hinhAnh,idtacGia,idnhaSanXuat,idLoaiSach,giaBan) values
('Lấy chồng quyền thế - Tập 11','Việc mang thai của Lâm Thiển được tất cả mọi người trong nhà quan tâm. Không chỉ ba mẹ chồng, ngay cả bà nội cũng đã vô cùng sốt ruột. Bà còn chuyển đến ở Thành Để, chăm sóc việc ăn uống và giám sát Cố Thành Kiêu và Lâm Thiển, khiến cuộc sống của hai vợ chồng trẻ trở nên dở khóc dở cười.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20757.jpg?v=1&w=340&h=497','7','1','nt','40000'),
('Vợ boss là công chúa - Tập 1','CÔNG CHÚA GIÁ ĐÁO! ảnh Y Nhân, vị công chúa 14 tuổi văn võ song toàn của nước Lộc Nguyên, “bỗng một ngày đen trời” bị hoàng đế cữu cữu ban thuốc độc vì tội là yêu nữ họa quốc rồi xuyên không thành bà xã 19 tuổi của tổng giám đốc tập đoàn Lục thị Đây là đâu? Đám độc phụ mặc đồ tang bắt cô uống thuốc này là định độc chết cô lần nữa sao? Cô là hoàng hoa khuê nữ, sao đột nhiên lại trở thành phụ nữ đã có chồng? Càng khó hiểu hơn là sao hoàng đế cữu cữu lại trở thành chồng cô thế này? Nơi kỳ lạ này có thật nhiều thứ mà cô không biết, đèn ngủ ở đây thổi không tắt, bị thương sẽ bị đưa vào “kỹ viện”, thêm cả chuyện “dì cả” đến thì cứ đến thôi, sao cô lại cần cái thứ “siêu thấm, siêu thoáng, siêu mềm mại” để làm gì?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20748.jpg?v=1&w=340&h=497','7','2','nt','43000'),
('Thực tập sinh thần tượng - Tập 16','Chẩm Khê đến Hàn Quốc du học và làm thực tập sinh dự bị của CL và phải trải qua ba tháng đào tạo để có cơ hội để trở thành thực tập sinh chính thức. Tại đây, cô phải đối mặt với sự bất đồng ngôn ngữ, khác biệt văn hóa cũng như phong cách sống, sự tẩy chay từ chính những đồng hương của mình và hơn thế là những cuộc thi sát hạch khốc liệt và cam go. Chẩm Khê tình cờ gặp được Lionel - một tiền bối trong CL, anh chàng này là tri kỷ và cũng là người giúp đỡ Chẩm Khê nhiều nhất khi cô còn ở Hàn. Vào ngày sinh nhật 18 tuổi của mình, Quả Tử Lê đã lặn lội sang Hàn Quốc để tìm gặp Chẩm Khê. Với sự cố chấp và tình yêu không có chút “tạp chất” của mình, Quả Tử Lê đã thành công khiến Chẩm Khê dao động và đồng ý làm bạn gái của cậu. Đồng thời, trong khoảng thời gian Chẩm Khê sang Hàn, cô vô tình biết được quá khứ và những chuyện mà Huy Dương giấu giếm mình, lần đầu tiên cô thật sự cảm thấy tủi thân và thất vọng về anh. Cũng từ đây mà quan hệ giữa Chẩm Khê và Huy Dương rơi vào bế tắc.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20793.jpg?v=1&w=340&h=497','4','1','nt','47000'),
('Tình nhân đẳng cấp thế giới - Tập 7','Một cuộc rượt đuổi khắp thế giới đã diễn ra. Ai là kẻ trốn chạy? Và ai là người cuồng si bỏ mặc mọi lý trí đi tìm? Dưới dải ngân hà rực rỡ ánh sao, một chuyện tình tuyệt đẹp đã diễn ra nhưng ngay buổi sáng hôm sau, một người đã vội vàng bỏ đi, bỏ lại một kẻ vô tội ngẩn ngơ…','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20610.jpg?v=1&w=340&h=497','7','2','nt','40000'),
('Nhất sinh nhất thế: Tiếu hồng trần - Tập 49','Cuộc chiến tranh giành lục địa và thống nhất giang sơn đã kết thúc với thỏa thuận hòa đàm giữa Nam Nhạc và Mạc Bắc, thế nhưng, các thế lực xung quanh vẫn không cam lòng và chưa chịu dừng tay. Mỗi một cuộc hội ngộ lại là điềm báo trước của chia ly. Dù luyến tiếc, nhưng không thể không buông tay. Thả cho Vàng về với đồng loại, Nam Cung Cẩm và Bách Lý Kinh Hồng lại tiếp tục chặng hành trình của mình. Một số nhân vật mới xuất hiện và những bất ngờ thú vị cũng theo cùng với họ. Họ là ai?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20657.jpg?v=1&w=340&h=497','7','2','nt','78000'),
('Điền viên cẩm tú - Tập 7','Mạc Vân Thiên cứu Tử La khỏi một con ngựa điên chạy loạn, lấy tên giả là Tần Vân xuất hiện trước mọi người. Huynh muội Tử La cùng Mạc Vân Thiên, Dung Phong, Chu Viễn và Lưu Hoành hẹn nhau đến Tây Giao cưỡi ngựa ngắm cảnh và đi dã ngoại. Tử La và Mạc Vân Thiên có cơ hội ở bên nhau nhiều hơn. Tình cảm của hai người dần thêm sâu sắc mà chính hai người cũng không nhận ra. Đúng lúc đó Mặc Vân Thiên lại rời đi, khiến Tử La không khỏi cảm thấy hụt hẫng. Không muốn để tiền nhàn rỗi, huynh muội Tử La quyết định đầu tư tiền mua điền trang và cửa hàng ở trấn trên, tạo ra một nguồn thu nhập ổn định. Mua cửa hàng, huynh muội Tử La khai trương bán một món ăn khá mới lạ cho tầng lớp trung hạ lưu. Liệu việc làm ăn buôn bán có thuận lợi?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20544.jpg?v=1&w=340&h=497','7','4','nt','43000'),
('Nhật ký cua trai của Nữ phụ - Tập 19','HỆ THỐNG CHỦ BỊ LỖI, PHONG QUANG KHÔNG CÓ THỰC THỂ. Điều gì sẽ xảy ra khi Hệ thống chủ - vật thể điều khiển, giám sát mọi nhiệm vụ của Phong Quang bị lỗi? Liệu Phong Quang có thể đến được thế giới giống như trong kịch bản cô đã chọn? Và cô phải làm như thế nào để có thể hoàn thành nhiệm vụ, quay trở lại không gian trắng kia?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20730.jpg?v=1&w=340&h=497','7','5','nt','49000'),
('Phượng Kinh Thiên - Tập 21','GẬY ÔNG ĐẬP LƯNG ÔNG. Bình Quốc Công muốn lấy Lữ Thục Viện về làm thiếp, việc này tưởng như đã được an bài, không ai dám phản đối. Tuy nhiên, đen đủi thay cho Bình Quốc Công phủ khi đột nhiên Vô Ưu công chúa lại ra mặt, che chở cho Lữ Thục Viện. Một bên nhất quyết muốn lấy, một bên nhất quyết không chịu trả người, vậy trước thánh chỉ ban hôn của Khánh Đế, Lữ gia, Hoài vương phủ, Bình Quốc Công phủ sẽ phải xử trí như thế nào? Hoài vương phát bệnh, Thái y trong cung ai lấy đều lắc đầu, liệu lần này, hắn còn có thể trở về từ Quỷ Môn Quan?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20604.jpg?v=1&w=340&h=497','7','4','nt','61000'),
('Nhật ký cua trai của Nữ phụ - Tập 6','CỨ NGỠ SẼ HẠNH PHÚC, HÓA RA LẠI BI THƯƠNG. “Nếu cần tìm một câu nói để hình dung tâm trạng lúc này của Tiết Nhiễm, thì đó chính là thanh tâm quả dục đã nhiều năm, chỉ ăn chay chưa từng ăn mặn, hôm nay bỗng dưng có một đĩa thức ăn mặn tuyệt hảo đặt trước mặt, có ăn món thịt đó hay không do hắn toàn quyền quyết định. Hơn nữa món thịt trước mặt hắn không phải là thịt bình thường, mà là một đĩa thịt biết mê hoặc hắn.” Rốt cuộc Phong Quang đã dùng cách gì để đưa người vốn không nhiễm bụi trần như Độc y thánh quân vào con đường tha hóa này? Liệu cô gái rụt rè, ngọt ngào của những thế giới trước khi đến thế giới này sẽ thay đổi như thế nào?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19744.jpg?v=1&w=340&h=497','7','5','nt','42000'),
('Bí mật vượt thời gian (Tập 1)','Bí mật vượt thời gian của Cô Bát là câu chuyện là câu chuyện về số phận trắc trở của Từ Nam Phương, cung phi của Hoàng đế Vạn Lịch. Khoảng cách trăm năm chỉ trong nháy mắt, duyên phận kỳ lạ đã đưa nàng đến tương lai 400 năm sau. Tại đây, nhiều biến cố ngang trái đã xảy ra, cuốn nàng vào vòng xoáy của tất cả âm mưu, tình yêu và thù hận.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/16902.jpg?v=1&w=340&h=497','5','2','nt','98000'),
('Nhất sinh nhất thế: Tiếu hồng trần - Tập 47','Chiến tranh tàn khốc dường như không có hồi kết. Sau vài lần ăn quả đắng, chó Hàn càng đánh càng hăng, càng đánh càng bất chấp, càng hiếu thắng và khát máu. Lần này, hắn đã khiến phe Hồng - Cẩm phải ngỡ ngàng khi lấy ra được thứ vũ khí bí mật tưởng chừng như không thể xuất hiện trên tay hắn. Thế nhưng, Nam Cung Cẩm cũng không phải là kẻ bị động như vậy.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20518.jpg?v=1&w=340&h=497','6','4','nt','43000'),
('Thực tập sinh thần tượng - Tập 13','Vì để chuyển hướng suy đoán và công kích của Sầm Nhiễm mà Vân Tụ đã đẩy Đoạn Ái Đình lên làm con tốt thí, nhưng không may, đúng lúc hai người đi cùng nhau thì đụng phải Chẩm Khê và Huy Dương đang đi chung. Đã vậy, Chẩm Khê còn bị Sầm Nhiễm lôi đi "bắt gian" Vân Tụ. Khi thấy cảnh Vân Tụ và Đoạn Ái Đình cùng nhau chơi vui vẻ tại trung tâm thương mạ, Sầm Nhiễm điên cuồng đánh Đoạn Ái Đình tới tấp, còn Chẩm Khê thì bỏ về.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20627.jpg?v=2&w=340&h=497','7','4','nt','43000'),
('Nàng dâu cực phẩm - Tập 55','Vì cứu Nhiễm Dao nên chân của Sở Kiêu bị thương nhẹ. Nhưng để tiếp cận được tiểu tiên nữ trong lòng nên anh vác chân đi bó bột, lấy cớ đó ép cô đồng ý cùng mình ôn tập thi công chức mỗi ngày trong vòng hai tháng. Là người rất nhạy cảm với sự lừa dối, nếu phát hiện ra sự thật Nhiễm Dao sẽ phản ứng thế nào?','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20567.jpg?v=1&w=340&h=497','7','4','nt','45000'),
('Trà trộn phòng con gái (Phần 2)','Chuyện kể về Lương Mân, một nhân viên cao cấp của nhà xuất bản An Mặc. Vì vô tình mà anh bị cô gái Tô Tô hiểu nhầm là gay, cô gái đã lôi anh về ở cùng căn hộ chung cư với ba cô gái khác. Đang lúc không có chỗ ở, nên anh chàng chấp nhận ở chung với bốn cô gái: Tô Tô, Linh Huyên, Hiểu Ngưng, Trình Lộ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/11555.jpg?v=2&w=340&h=497','3','5','nt','48000'),
('Boss là nữ phụ - Tập 41: Ác bá của Tinh Tế','Sau ba tháng hôn mê, xuyên không qua vô số thế giới, Thời Sênh đột ngột trở về thế giới thực – Thời đại Tinh Tế. Sự thức tỉnh của Thời Sênh khiến nhiều kẻ đang lăm le tiếm quyền phải từ bỏ ảo vọng. Dẹp bỏ các thế lực muốn nổi loạn trong Thời gia xong xuôi, Thời Sênh liền đi tìm Phượng Từ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/17864.jpg?v=2&w=340&h=497','3','4','nt','40000'),
('Kế hoạch theo đuổi vợ yêu - Tập 2','Cuộc sống sau khi sống lại của Tề Tiểu Tô đã hoàn thay đổi, gặp những chuyện mà cô tưởng như cả đời mình sẽ không bao giờ gặp phải. Tiểu Tô nghĩ rằng mình sẽ chăm chú học hành, thoát khỏi gia đình chú thím, bình an sống một đời, nhưng không... Cái Hệ thống chỉ ăn ngọc thạch kia khiến cô không thể không nghĩ cách kiếm tiền nuôi nó, đồng thời giúp cô cải thiện thể chất. Điều đó khiến cô phải bước đi trên một con đường không tầm thường.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/17740.jpg?v=2&w=340&h=497','3','4','nt','64000'),
('Boss là nữ phụ - Tập 45: Pháp sư vong linh','Vị diện tiếp theo, Thời Sênh xuyên vào Vong linh pháp giới vô cùng hỗn loạn. Thân thể Thời Sênh xuyên vào chính là Vĩ Huyền – kẻ đứng đầu phe pháp sư vong linh hắc ám. Nhận ra mình là một boss cứng, Thời Sênh rất vui vẻ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/18046.jpg?v=2&w=340&h=497','5','3','nt','90000'),
('Hệ thống livestream của Nữ Đế - Tập 21','Đám tân binh mới tuyển dám có ý nghĩ xấu xa về doanh trại nữ, bọn chúng cho rằng đó là doanh kỹ chuẩn bị cho chúng hưởng thụ. Không chỉ bọn chúng nghĩ thế mà những người đưa con gái vào doanh trại nữ cũng nghĩ thế.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/18918.jpg?v=1&w=340&h=497','4','2','nt','45000'),
('Dạo bước phồn hoa - Mở đầu','Nàng là một người mù sống trong bóng tối 20 năm... Cuối cùng bị người ta áp cho tội thông dâm rồi ban cho cái chết. Đến khi nàng mở mắt ra một lần nữa lại nhìn thấy thế giới muôn màu muôn sắc... Lật tay là thê lương, úp tay là phồn hoa... Hết thảy cuối cùng đều nằm trong một suy nghĩ của nàng...','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/20757.jpg?v=1&w=340&h=497','4','3','nt','44000'),
('Hệ thống livestream của Nữ Đế - Tập 25','Số lượng người chết trong chiến tranh quá nhiều, nhân số giảm mạnh rất bất lợi cho sự phát triển lâu dài. Điều này khiến Khương Bồng Cơ không thể không ban hành sắc lệnh khuyến khích phụ nữ trong độ tuổi sinh nở mang thai sinh con, với một tướng lĩnh Liên Bang như cô thì tội đó đủ để xử bắn.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19199.jpg?v=1&w=340&h=497','4','2','nt','40000');
insert into Sach(tenSach,moTa,ngonNgu,hinhAnh,idtacGia,idnhaSanXuat,idLoaiSach,giaBan) values
('Biết tuốt về thiên văn','Píp píp, mời lên tàu du hành vũ trụ! Tên tớ là BIẾT TUỐT. Tớ sẽ đưa cậu đi thám hiểm không gian. Đảm bảo cứ lật một trang mới, cậu sẽ phải “ồ, à” về những điều kỳ thú trong vũ trụ bao la của chúng mình. Đầu tiên chúng mình sẽ đi thăm tất cả các hành tinh trong Hệ mặt trời, từ Sao Thủy tới Sao Hải Vương. Rồi tớ sẽ đưa cậu tiến xa hơn tới Dải Ngân Hà và các thiên hà láng giềng khác. Vừa đi, tớ sẽ vừa giải thích cho cậu hiểu thế nào là Vành đai tiểu hành tinh, Tinh vân, hay Siêu tân tinh. Cậu còn được bật mí một chút về Hố đen nữa. Đó, tất cả những gì thường bị mọi người cho là khô khan, tớ sẽ giúp cậu thấy thú vị vì chúng mình sẽ VỪA HỌC VỪA CHƠI! Và khi mình tạm biệt nhau, tớ xin hứa danh dự với cậu, cậu sẽ biết tuốt về thiên văn cho xem!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19868.jpg?v=1&w=340&h=497','4','2','tn','45000'),
('Biết tuốt về đo lường','Đọc sách báo, cậu thấy ù hết cả tai với “mét”, “phít”, “in-sơ”. Trên ti-vi thì nói cái gì mà “bit” với “byte” ấy nhỉ. Lại còn trọng lượng và khối lượng, cùng là “lượng” sao tên lại khác nhau? Nào nào, yên tâm! Đi lấy ngay một cái kẹo mút đi nào. Khi có tớ ở bên, cậu sẽ thấy tất cả những thứ đó thật vui, thật ngọt ngào và thật dễ hiểu, như nhâm nhi một chiếc kẹo mút vậy! Khi kết thúc chuyến khám phá này, cậu sẽ biết cách đo chiều dài này, đo khối lượng, đo thời gian, đo độ mạnh/yếu này, lại còn biết cách chuyển đổi giữa các đơn vị đo nữa. Chúng mình sẽ cùng nhau VỪA HỌC VỪA CHƠI! Và khi mình tạm biệt nhau, tớ xin hứa danh dự với cậu, cậu sẽ biết tuốt về đo lường cho xem!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19800.jpg?v=1&w=340&h=497','4','2','tn','45000'),
('Biết tuốt về tiền bạc','Này, ti-vi hàng ngày vẫn nói những từ gì mà “ngân hàng”, “ngân quỹ”, “cổ phiếu”? Và còn bao nhiêu là thẻ nữa chứ, nào “thẻ ghi nợ” nào “thẻ tín dụng”? Thật khó hiểu. Nhưng cậu đừng vội lo lắng, từ từ tớ sẽ giúp cậu hiểu chúng ngay thôi. Có tớ ở bên, cậu sẽ thấy tất cả dễ như một cái búng tay ấy! Khi kết thúc chuyến khám phá này, cậu sẽ hiểu những từ cơ bản liên quan đến tiền như “kiếm tiền” này, “tiết kiệm”, “tiêu tiền” và “chia sẻ” này. Cậu cũng sẽ biết cách quản lý tiền thật tốt nữa. Chúng mình sẽ cùng nhau VỪA HỌC VỪA CHƠI! Và khi mình tạm biệt nhau, tớ xin hứa danh dự với cậu, cậu sẽ biết tuốt về tiền cho xem!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/19801.jpg?v=1&w=340&h=497','4','2','tn','46000'),
('Học toán qua truyện cổ tích - Cóc kiện Trời','Học toán qua truyện cổ tích - Cóc kiện trời là sản phẩm nằm trong bộ sách Học toán qua truyện cổ tích. Cuốn sách kể về hành trình của 7 con vật do Cóc dẫn đầu gồm: Gấu, Hổ, Cáo, Cua và bầy Ong tìm đường lên trời xin Ngọc Hoàng cho mưa xuống. Trải qua nhiều gian khổ trên đường đi, lại phải đương đầu với binh lính của nhà trời nhưng Cóc cùng các bạn đồng hành bằng sự mưu trí, tinh thần đoàn kết đã gặp được Ngọc Hoàng để xin mưa.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/17399.jpg?v=1&w=340&h=497','4','3','tn','56000'),
('Học toán qua truyện cổ tích - Cây tre trăm đốtc','Học toán qua truyện cổ tích - Cây tre trăm đốt là sản phẩm nằm trong bộ sách Học toán qua truyện cổ tích. Câu chuyện xoay quanh cuộc đời của anh Khoai, một thanh niên hiền lành, chăm chỉ và tốt bụng nhưng bị phú ông lợi dụng, vờ gả con gái cho để anh làm giàu cho lão. Với sự giúp đỡ của Bụt và hai câu thần chú “khắc nhập”, “khắc xuất”, anh Khoai không chỉ hoàn thành đem cây tre trăm đốt về theo yêu cầu của phú ông mà còn dạy cho lão một bài học nhớ đời. Câu chuyện Cây tre trăm đốt truyền tải đến các em triết lý ở hiền gặp lành, người tốt sẽ được giúp đỡ còn kẻ độc ác, gian xảo sẽ bị trừng trị. Cuốn sách cũng bồi dưỡng cho trẻ về giá trị văn hóa truyền thống, giá trị văn học của dân tộc.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/17396.jpg?v=1&w=340&h=497','5','3','tn','98000'),
('25 thói quen tốt để thành công','Cuốn sách 25 Thói Quen Tốt Để Thành Công của tác giả Yến Thúy Thúy chứa đựng nhiều thông tin có ích, nhiều bài học quý giá dành cho học sinh tiểu học. Cuốn sách nói về 25 thói quen tốt, với mỗi thói quen gồm 2 câu chuyện dẫn dắt và gợi mở cùng với lời khuyên hữu ích trong phần “Rèn luyện thói quen tốt”, sẽ giúp cho các em học sinh loại bỏ thói quen xấu, bồi dưỡng được những thói quen tốt để nắm bắt nhiều cơ hội thành công đồng thời cũng là tài liệu bổ ích hỗ trợ cho phụ huynh trong việc giáo dục trẻ.','Tiếng Việt','Cuốn sách 25 Thói Quen Tốt Để Thành Công của tác giả Yến Thúy Thúy chứa đựng nhiều thông tin có ích, nhiều bài học quý giá dành cho học sinh tiểu học. Cuốn sách nói về 25 thói quen tốt, với mỗi thói quen gồm 2 câu chuyện dẫn dắt và gợi mở cùng với lời khuyên hữu ích trong phần “Rèn luyện thói quen tốt”, sẽ giúp cho các em học sinh loại bỏ thói quen xấu, bồi dưỡng được những thói quen tốt để nắm bắt nhiều cơ hội thành công đồng thời cũng là tài liệu bổ ích hỗ trợ cho phụ huynh trong việc giáo dục trẻ.','5','5','tn','43000'),
('Sự tích dưa hấu','Truyện "Sự tích dưa hấu" thuộc nhóm truyện giải thích nguồn gốc của sự vật, ngoài ra còn có dị bản nữa mang tên là "Mai An Tiêm". Đây là câu chuyện cổ ngợi ca về sự chăm chỉ, khẳng định bản chất tự lực cánh sinh, tin vào sức lao động và sự bền bỉ kiên nhẫn sẽ mang lại hạnh phúc cho con người. Chính nhờ những nghị lực phi thường trong chính bản thân mình, chẳng những chàng Mai An Tiêm đã thoát khỏi trừng phạt của vua cha, mà còn nhân giống một loại cây quả quý giá là dưa hấu. Đây sẽ là một món quà đấy ý nghĩa cho các em nhỏ trên quãng đường phát triển và hình thành tính cách cho các em.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/6732.jpg?v=2&w=340&h=497','4','5','tn','41000'),
('Sự tích Sọ Dừa','Ở hiền gặp lành, đó là chân lý thường thấy trong những câu chuyện cổ tích. Sọ Dừa cũng không phải là ngoại lệ. Đây có lẽ được coi là một trong những câu chuyện dân gian hay và đầy ý nghĩa nhất trong kho tàng truyện cổ Việt Nam, và được rất nhiều các bạn nhỏ yêu thích.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/6729.jpg?v=2&w=340&h=497','4','3','tn','42000'),
('Công chúa phép thuật (Tập 1) - Chào mừng đến Alfea','Cuộc sống vẫn đều đều trôi qua với Bloom cho đến khi cô cứu một tiên nữ tên là Stella khỏi một con yêu tinh và phát hiện ra một sự thật bất ngờ: cô cũng là một tiên nữ! Bất ngờ nối tiếp bất ngờ, Bloom được theo học ở Học viện tiên nữ Alfea và lạc vào một thế giới phép thuật thú vị. Với sự giúp đỡ của Stella và những người bạn tiên nữ, Bloom đã học được cách sử dụng năng lượng phép thuật của mình để chiến đấu chống lại bộ ba phù thủy đang có âm mưu phá hủy ngôi trường.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/16793.jpg?v=1&w=340&h=497','2','4','tn','46000'),
('Thần thoại Hy Lạp - Tình yêu của các vị thần: Phép lạ của tình yêu','Thần thoại Hy Lạp - Tình yêu của các vị thần bao gồm các truyền thuyết về các vị thần và các anh hùng của người Hy Lạp. Qua những câu chuyện đặc sắc, trẻ em có cơ hội được tìm hiểu về văn hóa phương Tây và tiếp thu kiến thức về nhiều lĩnh vực khác nhau như ngôn ngữ, văn hóa, khoa học, nghệ thuật. Câu chuyện dễ hiểu, lối kể chuyện thú vị!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/13718.jpg?v=2&w=340&h=497','4','2','tn','52000'),
('Thần thoại Hy Lạp - Những anh hùng Hy Lạp: Chiến công hiển hách của các anh hùng Hy Lạp','Thần thoại Hy Lạp - Những anh hùng Hy Lạp bao gồm các truyền thuyết về các vị thần và các anh hùng của người Hy Lạp. Qua những câu chuyện đặc sắc, trẻ em có cơ hội được tìm hiểu về văn hóa phương Tây và tiếp thu kiến thức về nhiều lĩnh vực khác nhau như ngôn ngữ, văn hóa, khoa học, nghệ thuật… Câu chuyện dễ hiểu, lối kể chuyện thú vị!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/13689.jpg?v=2&w=340&h=497','2','5','tn','46000'),
('Thần thoại Hy Lạp - Những anh hùng Hy Lạp: Odysseus và hành trình trở về quê hương','Thần thoại Hy Lạp - Những anh hùng Hy Lạp bao gồm các truyền thuyết về các vị thần và các anh hùng của người Hy Lạp. Qua những câu chuyện đặc sắc, trẻ em có cơ hội được tìm hiểu về văn hóa phương Tây và tiếp thu kiến thức về nhiều lĩnh vực khác nhau như ngôn ngữ, văn hóa, khoa học, nghệ thuật… Câu chuyện dễ hiểu, lối kể chuyện thú vị!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/13713.jpg?v=1&w=340&h=497','4','2','tn','64000'),
('Cuộc săn lùng quái vật - Người núi khổng lồ Arcta','Chuyến đi tìm sự trợ giúp về cho ngôi làng của một cậu bé trở thành cuộc săn lùng giải cứu vương quốc. Từng nhân vật quái vật đáng sợ được khắc họa sinh động trong mỗi tập sách. Dưới lời nguyền của Phù thủy Hắc ám Malvel, người núi khổng lồ Arcta đã ra tay tàn phá thị trấn mà nó từng bảo vệ. Các anh hùng của chúng ta phải thẳng tiến đến vùng núi phương Bắc để giải thoát cho Arcta, đồng thời cứu thị trấn khỏi những trận bùn và đá lở trước khi quá muộn!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/13419.jpg?v=2&w=340&h=497','4','4','tn','34000'),
('Ngày xửa, ngày xưa - Chuyện ba chú gấu','Dành cho những em bé giàu trí tưởng tượng say mê những câu chuyện thần tiên,những em nhỏ đang háo hức tập đọc,những người lớn mơ mộng muốn trở về tuổi thơ và cho tất thảy những ai thích rèn luyện thói quen đọc sách từ nhỏ. Sách được minh họa bằng hình ảnh 3D,sống động tươi vui.giúp tạo ra được sự thích ','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/6939.jpg?v=2&w=340&h=497','4','5','tn','56000'),
('Những cuộc phiêu lưu của Shinbab','Dành cho những em bé giàu trí tưởng tượng say mê những câu chuyện thần tiên,những em nhỏ đang háo hức tập đọc,những người lớn mơ mộng muốn trở về tuổi thơ và cho tất thảy những ai thích rèn luyện thói quen đọc sách từ nhỏ. Sách được minh họa bằng hình ảnh 3D,sống động tươi vui.giúp tạo ra được sự thích thú,ham đọc và tìm tòi của trẻ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/6972.jpg?v=2&w=340&h=497','4','2','tn','45000'),
('Ngày xửa ngày xưa - Jack và cây đậu thần','Dành cho những em bé giàu trí tưởng tượng say mê những câu chuyện thần tiên,những em nhỏ đang háo hức tập đọc,những người lớn mơ mộng muốn trở về tuổi thơ và cho tất thảy những ai thích rèn luyện thói quen đọc sách từ nhỏ. Sách được minh họa bằng hình ảnh 3D,sống động tươi vui.giúp tạo ra được sự thích thú,ham đọc và tìm tòi của trẻ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/6957.jpg?v=2&w=340&h=497','4','3','tn','49000'),
('Thần thoại Hy Lạp - Tình yêu của các vị thần: Bài học của tình yêu','Thần thoại Hy Lạp - Tình yêu của các vị thần bao gồm các truyền thuyết về các vị thần và các anh hùng của người Hy Lạp. Qua những câu chuyện đặc sắc, trẻ em có cơ hội được tìm hiểu về văn hóa phương Tây và tiếp thu kiến thức về nhiều lĩnh vực khác nhau như ngôn ngữ, văn hóa, khoa học, nghệ thuật… Câu chuyện dễ hiểu, lối kể chuyện thú vị!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/13680.jpg?v=2&w=340&h=497','7','5','tn','46000'),
('Ngày xửa, ngày xưa - Alice lạc vào xứ sở thần tiên','Dành cho những em bé giàu trí tưởng tượng say mê những câu chuyện thần tiên,những em nhỏ đang háo hức tập đọc,những người lớn mơ mộng muốn trở về tuổi thơ và cho tất thảy những ai thích rèn luyện thói quen đọc sách từ nhỏ. Sách được minh họa bằng hình ảnh 3D,sống động tươi vui.giúp tạo ra được sự thích thú,ham đọc và tìm tòi của trẻ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/6936.jpg?v=2&w=340&h=497','7','4','tn','96000'),
('Ngày xửa, ngày xưa - Aladdin và cây đèn thần','Dành cho những em bé giàu trí tưởng tượng say mê những câu chuyện thần tiên,những em nhỏ đang háo hức tập đọc,những người lớn mơ mộng muốn trở về tuổi thơ và cho tất thảy những ai thích rèn luyện thói quen đọc sách từ nhỏ. Sách được minh họa bằng hình ảnh 3D,sống động tươi vui.giúp tạo ra được sự thích thú,ham đọc và tìm tòi của trẻ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/6933.jpg?v=2&w=340&h=497','7','5','tn','45000'),
('Kể chuyên gương hiếu học','Bộ sách được biên soạn công phu và khoa học, giúp các em nắm bắt khái quát những thông tin cơ bản nhất... thông qua đó như một bài học, một kinh nghiệm, những tấm gương người xưa phản chiếu để các em nỗ lực phấn đấu, hoàn thiện nhân cách ngày một tốt đẹp hơn.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.book/0/0/0/10380.jpg?v=2&w=340&h=497','7','5','tn','75000');
insert into Sach(tenSach,moTa,ngonNgu,hinhAnh,idtacGia,idnhaSanXuat,idLoaiSach,giaBan) values
('Hoàng tử bé - Tập 4','Khi Hoàng Tử Bé và Cáo đến thăm tộc người đá, thành phố của họ đạng bị đe dọa xâm chiếm bởi lũ dây gai. Tình thế buộc họ phải chạy trốn nhưng Nữ hoàng Cẩm thạch muốn cố thủ đến cùng. Trong khi đó, Mica, con trai của Nữ hoàng, vẫn chưa mang được hạt mầm đá về, thứ có thể bảo vệ cư dân đá khỏi lũ dây gai. Hoàng Tử Bé và Cáo phải lên đường tìm kiếm Mica trước khi mọi thứ trở nên quá muộn.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/486.jpg?v=1&w=365&h=540','4','3','tt','64000'),
('Thần mèo Pandada - Tập 3','Là tập cuối của series Thần mèo Pandada, tập 3 mang đến cho người đọc những tình huống thú vị và gần gũi nhất với cuộc sống con người, được thể hiện sinh động dưới những nét vẽ ngộ nghĩnh, đáng yêu','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/485.jpg?v=1&w=365&h=540','4','4','tt','61000'),
('Hoàng tử bé - Tập 3','Hoàng Tử Bé và Cáo đi đến Hành tinh Âm nhạc. Khi Hoàng Tử Bé đặt chân tới hành tinh này, những người Utiens có vẻ xáo trộn vì hành vi kì kỳ lạ của Euphonie, nữ danh ca hòa nhịp cho cuộc sống thường nhật của họ. Thời gian gần đây, Euphonie hát càng ngày càng tệ và có vẻ ưu tư. Khi Hoàng Tử Bé khám phá ra rằng “nỗi buồn” của Euphonie bắt nguồn từ tình yêu đầy mâu thuẫn của cô với Ivory, mọi chuyện dần trở nên phức tạp. Bởi Ivory là người của vương quốc Pistillaires, kẻ thù của dân tộc Utiens…','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/482.jpg?v=1&w=365&h=540','5','3','tt','60000'),
('Thần mèo Pandada - Tập 2','Nối tiếp nội dung hấp dẫn của tập 1, Thần Mèo Pandada tập 2 tiếp tục mang đến cho độc giả những câu chuyện thú vị về chú mèo ú DaDa đáng yêu cùng LaLa và hai trợ thủ đắc lực là BOBO BABA. Người đọc sẽ có được những giây phút thật tuyệt vời khi bắt gặp những tình huống hài hước với sự xuất hiện của siêu nhân và đại ca giang hồ, cùng trầm lại và đồng cảm với nỗi buồn của nhân vật cô gái yêu người lòng dạ đã đổi thay… cùng vô vàn câu chuyện thú vị khác. Không đơn thuần là cuốn truyện tranh giải trí, Thần Mèo Pandada 2 sẽ mang đến cho độc giả những triết lý sống sâu sắc.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/480.jpg?v=1&w=365&h=540','2','3','tt','70000'),
('Hoàng tử bé - Tập 2','Vẫn trong hành trình đuổi theo con Rắn độc ác, Hoàng Tử Bé và Cáo phát hiện ra một vùng đất bị lửa tàn phá. Theo lời kể của những người dân mài ngọc lục bảo sống lưu vong trên một chiếc vỏ ốc nổi, Chim lửa chính là kẻ đã gây ra điều này. Tuy vậy, Hoàng Tử Bé cho rằng để có thể biết được sự thật, không thể chỉ nhìn mọi thứ qua vẻ bề ngoài…','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/477.jpg?v=1&w=365&h=540','4','5','tt','67000'),
('Antidote','Một nhóm người bị bắt tham gia trò chơi sinh tử mang tên ANTIDOTE do tập đoàn Victoria tổ chức nhằm tìm ra người mang sức mạnh đặc biệt của nữ thần Serpah. Thế nhưng, càng về sau, trò chơi dần trở nên phức tạp hơn với sự can thiệp ngầm của các tổ chức bí ẩn đứng đằng sau nó.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/473.jpg?v=1&w=365&h=540','5','3','tt','74000'),
('Khỉ Zozo đạp xe - Tập 4','Khỉ Zozo Hiếu Kỳ là câu chuyện tuyệt vời dành cho thiếu nhi kể về các trò bắt chước và nghịch ngợm của một chú khỉ nhỏ. Tác giả là một nghệ sĩ tự học mà thành tài. Ông đã ở Paris đến tận những giờ phút cuối cùng trước khi quan đội Đức tiến vào đây vào ngày 14 tháng 6 năm 1940. Sau đó, ông và vợ mình đạp xe chạy trốn, chỉ mang theo mấy chiếc áo khoác mùa đông cùng vài cuốn truyện tranh buộc vào giá xe (có cả một ít màu nước và bản thảo Khỉ Zozo hiếu kỳ chưa xuất bản), vượt biên giới Pháp - Tây Ban Nha, đón tàu hỏa đi Lisbon rồi vượt biển Brazil.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/329.jpg?v=2&w=365&h=540','2','3','tt','71000'),
('Đường đua thần tốc - Tập 10','“Đường đua thần tốc” kể về những câu chuyện hết sức thú vị xảy ra trong cuộc sống hàng ngày của hai anh em sinh đôi là Lăng Phong và Lăng Vân. Tình cảm giữa hai anh em vô cùng gắn bó, hai người lại phối hợp với nhau rất ăn ý, học tập cũng rất xuất sắc, rất giỏi các môn thể thao như: đá bóng, bóng rổ, chạy bộ… Vì bố mẹ phải đi công tác nước ngoài khoảng nửa năm nên hai anh em Lăng Phong và Lăng Vân được gửi đến ở nhờ nhà cậu Hiểu Tùng một thời gian. Hai anh em được ghép vào lớp 8.3 của trường Trung học Dương Quang. Trong lớp này có một nhóm học sinh rất thích trò chơi đua xe điều khiển từ xa. Trong một môi trường hoàn toàn mới mẻ, cuộc sống của hai anh em sẽ xảy ra những câu chuyện thú vị gì?','Tiếng Việt','“Đường đua thần tốc” kể về những câu chuyện hết sức thú vị xảy ra trong cuộc sống hàng ngày của hai anh em sinh đôi là Lăng Phong và Lăng Vân. Tình cảm giữa hai anh em vô cùng gắn bó, hai người lại phối hợp với nhau rất ăn ý, học tập cũng rất xuất sắc, rất giỏi các môn thể thao như: đá bóng, bóng rổ, chạy bộ… Vì bố mẹ phải đi công tác nước ngoài khoảng nửa năm nên hai anh em Lăng Phong và Lăng Vân được gửi đến ở nhờ nhà cậu Hiểu Tùng một thời gian. Hai anh em được ghép vào lớp 8.3 của trường Trung học Dương Quang. Trong lớp này có một nhóm học sinh rất thích trò chơi đua xe điều khiển từ xa. Trong một môi trường hoàn toàn mới mẻ, cuộc sống của hai anh em sẽ xảy ra những câu chuyện thú vị gì?','6','3','tt','74000'),
('Anh bạn chơi quay tài ba - Tập 4','Trong trận bán kết, Hải Lực Bố để thua Ma Đa, vì vậy mà để mất con quay yêu quý của mình là Cực Quang Chiến Giáp. Vân Hiểu Hổ thề nhất định sẽ lấy lại con quay ấy. Ba Tư cử người gửi vũ khí bí mật cho A Báo, dụ A Báo chơi bẩn trong lúc thi đấu để giành thắng lợi trước Vân Hiểu Hổ. Nhưng qua quá trình đáu tranh tư tưởng quyết liệt, A Báo đã quyết định từ bỏ. Trong trận chung kết, Vân Hiểu hổ giành thắng lợi khá khó khăn trước Ma Đa, trở thành quán quân của giải đấu năm nay và giành lại được con quay Cực Quang Chién Giáp cho Hải Lực Bố. Sau trận quyết đấu, A Báo mời mọi người cùng về nhà cậu ở bộ tộc Báo chơi. Cùng lúc ấy, Ba Tử cũng cử Ma Đa dẫn theo Phong, Vũ, Lôi, Điện đến bộ tộc báo, truy tìm tung tích của con quay Phong Tiêu Liệt Báo','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/231.jpg?v=2&w=365&h=540','6','3','tt','84000'),
('Khỉ Zozo thả diều ','Khỉ Zozo Hiếu Kỳ là câu chuyện tuyệt vời dành cho thiếu nhi kể về các trò bắt chước và nghịch ngợm của một chú khỉ nhỏ. Tác giả là một nghệ sĩ tự học mà thành tài. Ông đã ở Paris đến tận những giờ phút cuối cùng trước khi quan đội Đức tiến vào đây vào ngày 14 tháng 6 năm 1940. Sau đó, ông và vợ mình đạp xe chạy trốn, chỉ mang theo mấy chiếc áo khoác mùa đông cùng vài cuốn truyện tranh buộc vào giá xe (có cả một ít màu nước và bản thảo Khỉ Zozo hiếu kỳ chưa xuất bản), vượt biên giới Pháp - Tây Ban Nha, đón tàu hỏa đi Lisbon rồi vượt biển Brazil.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/276.jpg?v=3&w=365&h=540','2','4','tt','78000'),
('Trúc Lâm đại sĩ - Tập 1','Linh nguyện cõi sống bao giờ cũng vậy. Đôi khi, giọt nắng vàng đọng trên mắt cũng đủ để thấy lại những tháng ngày mộng mị lênh đênh, chìm khuất trong đống tro tàn nguội lạnh của ký ức. Âm thanh của giọt nước vỡ trên đám rêu xanh cũng đủ để nghe thấy bản tình ca điêu linh vĩ đại về ảo ảnh ngày mai. Thực sống với chính mình, trong nỗi cô đơn thầm lặng dằn xé tê dại một kiếp người. Để rồi, trong tương giao giữa vạn vật, ở đấy, tâm trí bỗng thăng hoa.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/101.jpg?v=2&w=365&h=540','5','3','tt','94000'),
('Tủ sách chồi non - Tình bạn của đôi guốc','Cuốn sách nằm trong Tủ Sách Chồi Non - bộ sách mang đến cho các bé niềm vui đọc sách, đồng hành cùng tuổi thơ qua những câu chuyện, giúp các bé học hỏi và lớn khôn từng ngày Tủ Sách Chồi Non được viết bởi những tác giả nổi tiếng với những câu chuyện thiếu nhi cuốn hút, từng rung động bao trái tim con trẻ, kể cả những người lớn đã-từng-là-trẻ-con...; cùng hình ảnh minh họa sinh động, nét vẽ hiện đại của các họa sĩ trẻ đam mê sách tranh sẽ kích thích trí tưởng tượng phong phú, bay cao bay xa cho các bé.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/580.jpg?v=1&w=365&h=540','5','3','tt','44000'),
('Tủ sách chồi non - Sừng To Sừng Nhỏ','Cuốn sách nằm trong Tủ Sách Chồi Non - bộ sách mang đến cho các bé niềm vui đọc sách, đồng hành cùng tuổi thơ qua những câu chuyện, giúp các bé học hỏi và lớn khôn từng ngày. Tủ Sách Chồi Non được viết bởi những tác giả nổi tiếng với những câu chuyện thiếu nhi cuốn hút, từng rung động bao trái tim con trẻ, kể cả những người lớn đã-từng-là-trẻ-con...; cùng hình ảnh minh họa sinh động, nét vẽ hiện đại của các họa sĩ trẻ đam mê sách tranh sẽ kích thích trí tưởng tượng phong phú, bay cao bay xa cho các bé.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/486.jpg?v=1&w=365&h=540','4','3','tt','64000'),
('Chú lợn biết bay','Chú lợn “Ụ Đất” trong câu chuyện này sẽ làm gì khi chú được sinh ra với một đôi cánh và mang trong mình ước mơ rằng một ngày nào đó chú có thể bay? Bằng niềm tin và lòng dũng cảm, hãy thực hiện ước mơ của chính mình!','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/558.jpg?v=1&w=365&h=540','6','2','tt','73000'),
('Người nông dân và các con trai','Truyện ngụ ngôn Aesop được truyền miệng trong dân gian qua nhiều thế hệ và nhiều nền văn hoá. Trong quá trình lưu truyền đó, một số truyện đã bị mất đi nhưng cũng có một số truyện được thêm vào từ các nền văn hoá khác nhau, thể hiện sự ngưỡng mộ của nhân loại đối với trí tuệ sâu sắc của ông.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/553.jpg?v=1&w=365&h=540','7','2','tt','64000'),
('Ngỗng và rùa','Tuyển Tập Truyện Tranh Aesop là bộ truyện tranh phỏng theo những câu chuyện ngụ ngôn Aesop. Nội dung được trình bày bằng hình thức truyện tranh sinh động, sắc nét, giúp làm tăng hứng thú cho độc giả, đặc biệt là các em nhỏ.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/547.jpg?v=1&w=365&h=540','6','2','tt','24000'),
('Con lừa và con chó nhỏ','Aesop đã để lại cho nhân loại một kho tàng truyện ngụ ngôn đồ sộ cả về mặt số lượng lẫn giá trị. Với đa số nhân vật là những con vật đã được nhân cách hoá, truyện ngụ ngôn Aesop hàm chứa những thông điệp sâu sắc mà giản dị, được chuyển tải đến người đọc bằng giọng văn nhẹ nhàng, hóm hỉnh. Mỗi câu chuyện của Aesop là một bài học vẫn còn nguyên giá trị giáo dục cho cuộc sống hôm nay, giúp chúng ta có cái nhìn rõ hơn về thế giới, về xã hội, giúp chúng ta hiểu biết hơn và cư xử với nhau tốt hơn trong cuộc sống.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/540.jpg?v=1&w=365&h=540','7','2','tt','67000'),
('Con gấu và hai người bạn','Aesop đã để lại cho nhân loại một kho tàng truyện ngụ ngôn đồ sộ cả về mặt số lượng lẫn giá trị. Với đa số nhân vật là những con vật đã được nhân cách hoá, truyện ngụ ngôn Aesop hàm chứa những thông điệp sâu sắc mà giản dị, được chuyển tải đến người đọc bằng giọng văn nhẹ nhàng, hóm hỉnh. Mỗi câu chuyện của Aesop là một bài học vẫn còn nguyên giá trị giáo dục cho cuộc sống hôm nay, giúp chúng ta có cái nhìn rõ hơn về thế giới, về xã hội, giúp chúng ta hiểu biết hơn và cư xử với nhau tốt hơn trong cuộc sống.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/536.jpg?v=1&w=365&h=540','3','5','tt','94000'),
('50 câu ca dao - Tập 2','ừ xa xưa, ông cha ta đã sáng tạo nên những lời thơ hết sức trữ tình để gửi gắm vào trong những câu ca dao dân gian. Đó có thể chỉ là vài câu ngâm nga trong những bài hát ru ầu ơ của mẹ, cũng có thể tựa như một bài hát dài. Chúng đã diễn tả đời sống của nhân dân theo cách thật giản dị và sinh động nhất, và đã trở thành một phần không thể thiếu trong kho tàng nghệ thuật của Việt Nam.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/524.jpg?v=1&w=365&h=540','6','2','tt','44000'),
('Có một con mọt sách','Cuốn sách gồm 7 truyện tranh ngắn và gần gũi: Có một con mọt sách; Cá bảy màu; Giếng nước mùa xuân; Một cuộc du lịch kỳ quái; Có “Chí” thì… hư; Cái mũi để chi; Nghỉ hè, nên làm gì?. Điều thú vị ở tập sách nhỏ này chính là những kiến thức bổ ích về sức khỏe, về vệ sinh cá nhân được bác sĩ Đỗ Hồng Ngọc lồng ghép trong những câu chuyện nhẹ nhàng. Như trong truyện được chọn làm tiêu đề cho tập sách - Có một con mọt sách, các bé sẽ được nghe câu chuyện về cậu bé Sinh đam mê đọc sách nhưng đọc không đúng kỹ thuật nên bị biến thành con mọt, phải bò trên trang sách. Vì vậy, các bé muốn không bị biến thành con mọt sách thì nên đọc sách đúng kỹ thuật và luôn vệ sinh mắt đúng cách. Điều quan trọng nữa là các bé cần được hướng dẫn chọn lựa những sách tốt, sách có kiến thức bổ ích.','Tiếng Việt','https://307a0e78.vws.vegacdn.vn/view/v2/image/img.comic/0/0/0/507.jpg?v=1&w=365&h=540','1','4','tt','64000');
